INSERT INTO products (slug, title, description, price_cents, currency, tags, region_code, images, in_stock) VALUES
('brie-de-meaux', 'Brie de Meaux AOP', 'Король французских сыров! Классический мягкий сыр с бархатистой белой плесневой корочкой и сливочной кремовой текстурой. Бри де Мо родом из региона Иль-де-Франс обладает нежным грибным вкусом с легкой кислинкой. Идеально сочетается с белым хлебом, грушами и игристыми белыми винами. Возраст созревания 4-8 недель. Имеет статус AOP (контролируемое наименование по происхождению).', 2500, 'RUB', ARRAY['soft', 'bloomy', 'creamy', 'french'], 'FR', ARRAY['/images/brie-de-meaux-1.jpg'], true),
('parmigiano-reggiano', 'Пармиджано-Реджано DOP 24 месяца', 'Величественный итальянский король сыров, выдержанный 24 месяца в традиционных пещерах Эмилии-Романьи. Твердый сыр с кристаллической текстурой и ореховым вкусом. Каждый круг весит 30-40 кг и производится по строгому рецепту с 1200 года. Идеален для натирания на пасту, добавления в ризотто или просто как самостоятельная закуска с медом. Настоящий Parmigiano-Reggiano DOP имеет клеймо с пунктирной надписью.', 3200, 'RUB', ARRAY['hard', 'aged', 'nutty', 'italian', 'grated'], 'IT', ARRAY['/images/parmigianoreggiano.png'], true),
('manchego-curado', 'Манчего Кюрадо 12 месяцев', 'Испанский овечий сыр из региона Ла-Манча, выдержанный 12 месяцев. Обладает насыщенным ореховым вкусом с солоноватыми нотами и плотной маслянистой текстурой. Узнаваем по характерному рисунку плетеной корзины на корке. Манчего производят из молока овец породы Манчега с 1605 года. Отлично сочетается с красным вином и орехами.', 2800, 'RUB', ARRAY['sheep', 'semi-hard', 'spanish', 'nutty'], 'ES', ARRAY['/images/манчего.png'], true),
('gruyere', 'Грюйер AOP', 'Швейцарский альпийский сыр с богатым сложным вкусом. Комбинация орехового, фруктового и слегка соленого вкуса создает неповторимую гастрономическую гармонию. Выдержка 5-12 месяцев в пещерах. Грюйер родом из швейцарского кантона Фрибург и производится из непастеризованного коровьего молока. Идеален для фондю, а также как изысканная закуска с вином.', 3100, 'RUB', ARRAY['swiss', 'alpine', 'hard', 'nutty', 'fruity'], 'CH', ARRAY['/images/gryuyer_aop.jpg'], true),
('comte-cheese', 'Сыр Комте AOP', 'Французский сыр из региона Франш-Конте, производимый из сырого коровьего молока. Выдержка 8-24 месяца придает ему ореховый и фруктовый вкус. Традиционно производится в пещерах с июня по сентябрь. Имеет статус AOP с 1958 года. Идеален с белыми винами и сухофруктами.', 2900, 'RUB', ARRAY['cheese', 'aged', 'french', 'nutty', 'fruity'], 'FR', ARRAY['/images/Комте.png'], true),
('camembert-normandie', 'Камамбер де Нормандия AOP', 'Мягкий французский сыр с белой плесневой коркой родом из Нормандии. Обладает земляным вкусом с грибными нотами и нежной сливочной консистенцией внутри. Традиционный камамбер производится из сырого молока и имеет диаметр 11 см. Изобретен в 1791 году Мари Арель. Идеален с багетом и яблоками.', 2200, 'RUB', ARRAY['soft', 'french', 'earthy', 'mushroomy'], 'FR', ARRAY['/images/brie.png'], true),
('pecorino-romano', 'Пекорино Романо DOP', 'Итальянский овечий сыр с острым соленым вкусом, традиционно используемый для натирания над пастой. Родом из Лацио, производится из овечьего молока породы Сарда. Выдержка 8-12 месяцев. Имеет плотную текстуру и острый вкус. Исторически его хранили воины Древнего Рима как источник белка в длительных походах.', 2600, 'RUB', ARRAY['sheep', 'italian', 'hard', 'grated', 'salty'], 'IT', ARRAY['/images/parmigianoreggiano.png'], true),
('iberico-ham', 'Хамон Иберико де Белота', 'Легендарный испанский хамон из 100% иберийских свиней, откормленных желудями (белотой). Высшая категория хамона с мраморной структурой и неповторимым орехово-карамельным вкусом. Процесс вяления 36-48 месяцев в уникальном климате Сьерры. Содержит олеиновую кислоту, сопоставимую с оливковым маслом.', 8500, 'RUB', ARRAY['ham', 'cured', 'spanish', 'acorn-fed', 'premium'], 'ES', ARRAY['/images/Хамон_Родригес_Иберико_без_кости.png'], true),
('serrano-ham', 'Хамон Серрано', 'Классический испанский вяленый хамон из белой свиньи, выдержанный 12-14 месяцев. Нежный солоноватый вкус и ароматная текстура. Идеален для нарезки тонкими ломтиками. Традиционный испанский деликатес, подается на хлебе с оливковым маслом или с дыней.', 3800, 'RUB', ARRAY['ham', 'cured', 'spanish', 'sliced'], 'ES', ARRAY['/images/Хамон Родригес Серрано.png'], true),
('dorblu', 'Дорблю', 'Немецкий голубой сыр с нежным сливочным вкусом и характерными голубыми прожилками плесени. Производится в Баварии по традиционному рецепту с использованием благородной плесени Penicillium roqueforti. Обладает мягким солоноватым вкусом с ореховыми нотами. Идеален для салатов, соусов и как самостоятельная закуска с медом и орехами.', 3400, 'RUB', ARRAY['blue', 'german', 'creamy', 'mild'], 'DE', ARRAY['/images/Дорблю.png'], true),
('gorgonzola-dolce', 'Горгонзола Dolce DOP', 'Мягкий итальянский голубой сыр с кремовой текстурой и нежным сладковатым вкусом. В отличие от крепкого Piccante, Dolce созревает 50-60 дней, что придает ему мягкость. Из региона Ломбардия. Идеален для ризотто, пасты или как намазка на хлеб. Содержит благородную плесень Penicillium glaucum.', 2700, 'RUB', ARRAY['blue', 'italian', 'creamy', 'sweet'], 'IT', ARRAY['/images/Gorgonzola.jpg'], true),
('parmigiano-reggiano-24', 'Parmigiano Reggiano 24 mesi', 'Классический выдержанный пармезан с богатым вкусом умами. Выдержка 24 месяца в традиционных пещерах Эмилии-Романьи. Идеален для пасты, ризотто и салатов. Каждый круг весит 30-40 кг и производится по строгому рецепту с 1200 года.', 3300, 'RUB', ARRAY['сыр', 'твёрдый', 'умами', 'паста', 'италия'], 'IT', ARRAY['/images/grana.png'], true),
('truffle-oil', 'Трюфельное масло 250мл', 'Ароматное оливковое масло первого холодного отжима с добавлением настоящего белого трюфеля из региона Умбрия. Интенсивный земляной аромат и изысканный вкус. Добавь в пасту, ризотто или картофель для создания блюд премиум-класса. Производится в небольших партиях с соблюдением традиционных технологий.', 1800, 'RUB', ARRAY['масло', 'трюфель', 'аромат', 'паста', 'деликатес'], 'IT', ARRAY['https://placehold.co/800x800/E6E6FA/9370DB?text=Truffle+Oil'], true),
('iberico-ham-block', 'Хамон Иберико Блок 4.5кг', 'Премиальный испанский хамон из иберийских свиней. Блок весом 4.5 кг с неповторимым орехово-карамельным вкусом и мраморной текстурой. Процесс вяления 24-36 месяцев. Идеален для профессиональной нарезки тонкими ломтиками. Подается как самостоятельное блюдо или с хлебом и оливковым маслом.', 4800, 'RUB', ARRAY['ham', 'cured', 'spanish', 'premium', 'block'], 'ES', ARRAY['/images/Хамон_Родригес_Иберико_Блок_4_5кг-.png'], true),
('camembert-aop', 'Camembert AOP', 'Мягкий французский сыр с белой плесенью родом из Нормандии. Сливочный и нежный вкус с грибными нотами и земляным ароматом. Производится из сырого коровьего молока по традиционному рецепту с 1791 года. Подаётся с багетом и белым вином для идеального сочетания.', 2400, 'RUB', ARRAY['сыр', 'плесень', 'мягкий', 'сливочный', 'вино'], 'FR', ARRAY['/images/brie.png'], true),
('brie-de-meaux-aop-extended', 'Brie de Meaux AOP расширенный', 'Легендарный французский бри с лёгким ореховым послевкусием и нежной текстурой. Король французских сыров родом из региона Иль-де-Франс. Бархатистая белая плесневая корочка и кремовая консистенция. Возраст созревания 4-8 недель. Имеет статус AOP и признан одним из лучших сыров мира.', 2600, 'RUB', ARRAY['сыр', 'мягкий', 'франция', 'вино', 'ореховый'], 'FR', ARRAY['/images/brie.png'], true),
('chorizo-espanol', 'Chorizo Español Picante', 'Острая испанская колбаса с паприкой традиционного рецепта. Интенсивный ароматный вкус с дымной пикантностью. Отлично к сырам и в тапас. Производится из свинины высшего качества с добавлением паприки и чеснока. Выдержка в естественных условиях создает уникальный вкус.', 2200, 'RUB', ARRAY['колбаса', 'острое', 'испания', 'антипасти', 'мясо'], 'ES', ARRAY['https://placehold.co/800x800/FFE4CD/CD853F?text=Chorizo+Picante'], true),
('feta-pdo', 'Фета PDO', 'Традиционный рассольный сыр из овечьего молока с характерным солоноватым вкусом и зернистой текстурой. Производится в Греции по защищенному наименованию PDO. Хорош в салатах и пирогах, идеален с оливками и томатами. Свежий и полезный продукт средиземноморской кухни.', 1600, 'RUB', ARRAY['сыр', 'рассольный', 'салат', 'греческий', 'умами'], 'GR', ARRAY['https://placehold.co/800x800/FFF8DC/E5D5A3?text=Feta+PDO'], true),
('olives-kalamata', 'Оливки Каламата', 'Мясистые тёмные оливки из региона Каламата с насыщенным фруктовым вкусом и маслянистой текстурой. Традиционный продукт греческой кухни с характерным ароматом. Отлично подходят для антипасти, салатов и в качестве гарнира к мясным блюдам. Консервированы в оливковом масле.', 1400, 'RUB', ARRAY['оливки', 'греческие', 'антипасти', 'салат', 'гарнир'], 'GR', ARRAY['https://placehold.co/800x800/E5D5A3/D4AF74?text=Olives+Kalamata'], true),
('olive-oil-crete', 'Оливковое масло Extra Virgin Крит', 'Оливковое масло первого холодного отжима с острова Крит. Высококачественный продукт с травянистыми нотами и деликатным фруктовым вкусом. Подходит для заправки салатов и приготовления блюд. Содержит многочисленные полезные вещества и витамины.', 2200, 'RUB', ARRAY['масло', 'оливковое', 'греческое', 'салат', 'органик'], 'GR', ARRAY['https://placehold.co/800x800/E5D5A3/8B6F47?text=Olive+Oil+Crete'], true),
('balsamic-modena', 'Бальзамический уксус Модены IGP', 'Выдержанный уксус из Модены с карамельными нотами и сладким послевкусием. Выдерживается в деревянных бочках не менее 12 лет. Подходит для сыров и мяса, идеален для заправки салатов. Обладает густой консистенцией и насыщенным вкусом.', 1900, 'RUB', ARRAY['уксус', 'модена', 'соус', 'салат', 'итальянский'], 'IT', ARRAY['https://placehold.co/800x800/2D1810/8B4513?text=Balsamic+Modena'], true),
('pesto-genovese', 'Песто Дженовезе', 'Свежий базиликовый соус с пармезаном и кедровыми орехами родом из Генуи. Классический рецепт итальянской кухни с насыщенным травяным ароматом. Идеален для пасты, пиццы и в качестве намазки. Приготовлен из свежего базилика, оливкового масла и качественного пармезана.', 1200, 'RUB', ARRAY['соус', 'базилик', 'паста', 'умами', 'италия'], 'IT', ARRAY['https://placehold.co/800x800/228B22/87CEEB?text=Pesto+Genovese'], true);
